mapper.select:
        select
         A.*,B.file_url
        from room_details A left join room_file B on A.room_id=B.room_details_id
        
            
            
                and A.district_id=#{districtid}
            
            
                and A.houseprice >=#{roomPriceMin} and A.houseprice <=#{roomPriceMax}
            
            
                and (A.houseprice * A.buildarea) >=#{roomPriceMin} and (A.houseprice * A.buildarea) <=#{roomPriceMax}
            
            
              and A.housearea >=#{roomAreaMin} and A.housearea <=#{roomAreaMax}
            
            
                and A.market_type=#{roomMarketStatus}
            
            
                and A.tenement_type=#{roomTenementType}
            
            
               and A.market_time >=#{roomMarketTimeMin} and A.market_time <=#{roomMarketTimeMax}
            
            
              and A.finish_type=#{roomDecorationType}
            
            group by A.room_id
            
                order by A.houseprice asc
            
            
                order by A.houseprice desc
            
            
                order by A.market_time asc
            
            
                order by A.market_time desc
