mapper.select:
select

from room_new_details



       and name  like "%"#{name}"%"


        and sale_date >=#{beginSaleDate} and sale_date <= #{endSaleDate}


        and finished_date >=#{beginFinishedDate} and finished_date <= #{endFinishedDate}


        and status=#{status}


        and house_price >=#{minHousePrice} and house_price <= #{maxHouserPrice}
