mapper.select:
select

from room_two_details



       and districtid=#{districtid}


       and name like '%${name}%'


       and price_total >=#{priceMin} and price_total <=#{priceMax}


       and house_area >=#{houseAreaMin} and house_area <=#{houseAreaMax}


       and hot_recommend=#{hotRecommend}


       and project_advantage like '%${projectAdvantage}%'
